import L from 'leaflet';

const createPointIcon = () => {
    return L.icon({
        iconUrl: 'dron.png.png', // ścieżka do ikony
        iconSize: [32, 32], // rozmiar ikony
        iconAnchor: [16, 32], // punkt kotwiczenia ikony
        popupAnchor: [0, -32] // punkt kotwiczenia popupu względem ikony
    });
};

export default createPointIcon;
