import React, { useEffect, useState } from 'react';
import './Map.css';
import { MapContainer, TileLayer, LayersControl, GeoJSON, Marker } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

import MarkerPlacement from './MarkerPlacement';
import axios from 'axios';
import createPointIcon from './createPointIcon';

function Map() {
    const [jgrbudynki, setJgrbudynki] = useState();

    const makePopup = (feature, layer) => {
        if (feature) {
            layer.bindPopup(
                `<h1>${feature.properties.JRG}</h1>
        Specjalizacja: ${feature.properties.specjaliza}
        <p> Dowódca JRG: ${feature.properties.dowodcaJRG} </p>
        <p> Zastępca Dowódcy JRG: ${feature.properties.zastepcado} </p>
        <p> Adres: ul. ${feature.properties.ulica}, ${feature.properties.kodpocztow} ${feature.properties.miasto}</p>
        <p> Telefon: ${feature.properties.telefon} </p>
        <p> Email: ${feature.properties.email} </p>
      `
            );
        }
    };

    useEffect(() => {
        const getData = () => {
            axios
                .get(`http://localhost:8080/geoserver/prgeo/ows?service=WFS&version=1.0.0&request=GetFeature&typeName=prgeo%3Ajgrbudynki&maxFeatures=50&outputFormat=application%2Fjson`)
                .then((dane) => {
                    console.log(dane.data);
                    setJgrbudynki(dane.data);
                })
                .catch((error) => {
                    console.log(error);
                    return null;
                });
        };
        getData();
    }, []);

    const pointIcon = createPointIcon();

    return (
        <div className='map_main'>
            <MapContainer center={[52.232222, 21.008333]} zoom={15}>
                <LayersControl position='topright'>
                    {/* Warstwy */}
                </LayersControl>

                {/* Warstwa GeoJSON */}
                <GeoJSON data={jgrbudynki} onEachFeature={makePopup} />

                {/* Punkty */}
                {jgrbudynki && jgrbudynki.features.map((feature, index) => (
                    <Marker key={index} position={[feature.geometry.coordinates[1], feature.geometry.coordinates[0]]} icon={pointIcon} />
                ))}

                {/* MarkerPlacement */}
                <MarkerPlacement />
            </MapContainer>
        </div>
    );
}

export default Map;
