import React, { useEffect, useState } from "react";
import "./Services.css";
import { Link } from "react-router-dom";
import Swiper from 'swiper';
import 'swiper/swiper-bundle.css';
import ModalImage from "react-modal-image";

import ListIcon from '@mui/icons-material/List';
import Button from '@mui/material/Button';
import MapIcon from '@mui/icons-material/Map';

function Services() {
    useEffect(() => {
        const swiper = new Swiper('.swiper-container', {
            direction: 'horizontal',
            mousewheel: {},
            effect: 'cube',
            keyboard: {
                enabled: true,
                onlyInViewport: false
            },
            slidesPerView: 3, // Liczba slajdów do wyświetlenia jednocześnie
            spaceBetween: -1, // Odległość między slajdami
            loop: true // Powtarzanie slajdów
        });
    }, []);

    const [modalOpen, setModalOpen] = useState(false);
    const [selectedImage, setSelectedImage] = useState(null);

    const handleImageClick = (index) => {
        setSelectedImage(index);
        setModalOpen(true);
    };

    const handleCloseModal = () => {
        setSelectedImage(null);
        setModalOpen(false);
    };

    return (
        <div className="services_main">
            <div className="services_top">
                <h1 className="services_title">DOSTĘPNE USŁUGI</h1>
            </div>
            <div className="swiper-container">
                <div className="swiper-wrapper">
                    <div className="swiper-slide" onClick={() => handleImageClick(0)}>
                        <img src="https://cdn.galleries.smcloud.net/t/galleries/gf-kgDe-irhH-K897_ogien-pozar-664x442.jpg" alt="Slide 1" />
                    </div>
                    <div className="swiper-slide" onClick={() => handleImageClick(1)}>
                        <img src="https://cdn.galleries.smcloud.net/t/galleries/gf-kgDe-irhH-K897_ogien-pozar-664x442.jpg" alt="Slide 2" />
                    </div>
                    <div className="swiper-slide" onClick={() => handleImageClick(2)}>
                        <img src="https://cdn.galleries.smcloud.net/t/galleries/gf-kgDe-irhH-K897_ogien-pozar-664x442.jpg" alt="Slide 3" />
                    </div>
                    {/* Dodaj inne slajdy tutaj */}
                </div>
            </div>

            <div className="services_bottom">
                <div className="services_left">
                    <Link to="map">
                        <button className="services_button"><MapIcon /> Mapa</button>
                    </Link>

                    <Link to="bazabsp">
                        <button className="services_button"><ListIcon /> Baza BSP</button>
                    </Link>

                    <Link to="../">
                        <button className="home_about">STRONA GŁÓWNA</button>
                    </Link>
                </div>
                <div className="services_footer"></div>
            </div>

            {modalOpen && (
                <div className="modal-container">
                    <ModalImage
                        className="modal-image"
                        small={selectedImage}
                        large={selectedImage}
                        alt="Modal Image"
                        onClose={handleCloseModal}
                    />
                </div>
            )}
        </div>
    );
}

export default Services;
